import React, {useEffect, useState} from 'react';
import {Link} from 'react-router-dom';
import { Button } from './Button';
import "./Navbar.css";

function Navbar() {
  const [click,setClick] = useState(false);
  const [button,setbutton] = useState(true)

  const handleClick =() => setClick(!click)
  const closeMobileMenu =() => setClick(false)

  
  const showButton = () => {
    if(window.innerWidth <= 960){
      setbutton(false)
    }else {
      setbutton(true)
    }
  }

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize',showButton)

  return (
    <>
    <nav className='navbar'>
      <div className='navbar-container'>
        <Link to ='/' className='navbar-logo' onClick={closeMobileMenu}>
          ALI 
          <i className='fab fa-typo3'/>
        </Link>
        <div className="menu-icon" onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
        </div>
        <ul className={click ? 'nav-menu active' : 'nav-meny'}>
          
          <li className='nav-item'>
            <Link onClick={closeMobileMenu} className ='nav-links' to="/">Home </Link>
          </li>
          <li className='nav-item'>
            <Link onClick={closeMobileMenu} className ='nav-links' to="./Products">Products </Link>
          </li>

          <li className='nav-item'>
            <Link onClick={closeMobileMenu} className ='nav-links' to="./Services">Services </Link>
          </li>

          <li className='nav-item'>
            <Link onClick={closeMobileMenu} className ='nav-links-mobile' to="./Sign-up ">Sign Up </Link>
          </li>

        </ul>
        {button && <Button buttonStyle={'btn--outline'} >SIGN UP</Button>}
      </div>
    </nav>
    </>
  )
}

export default Navbar
